import React from 'react'
import { Stack, Typography} from "@mui/material";
import {makeStyles} from "@mui/styles";

const useStyles = makeStyles((theme) => ({
    textStack: {
        [theme.breakpoints.up('md')]: {
            alignContent: "center",
            justifyContent: "center",
            textAlign: "center"
        }
    }
}));

function Headline() {
    return (
        <Typography
            variant="h3"
        >
            social media, for sports clubs
        </Typography>
    )
}

function Caption() {
    return (
        <Typography
            variant="h6"
            maxWidth="10%"
        >
            A new take on managing your sports club where you can digitally take control of your sports club and better drive member engagement.
        </Typography>
    )
}

export default function PromoText() {
    const classes = useStyles();

    return (
        <Stack
            className={classes.textStack}
            direction="column"
            height="90vh"
            width="100%"
        >
            <Headline />
            <Caption />
        </Stack>
    )
}
