import React from 'react'
import {Box, Grid, Paper, Stack, Typography} from "@mui/material";
import {makeStyles} from "@mui/styles";
import Image from 'mui-image'
import HomeImage from "../assets/img/image_home.png"

const useStyles = makeStyles((theme) => ({
    textStack: {
        [theme.breakpoints.up('md')]: {
            alignContent: "center",
            justifyContent: "center",
            textAlign: "center"
        }
    }
}));

function Headline() {
    return (
        <Typography
            variant="h3"
            fontSize={100}
            fontWeight={600}
            bgcolor="green"
        >
            social media, for sports clubs
        </Typography>
    )
}

function Caption() {
    return (
        <Typography
            variant="h6"
            fontSize={28}
            fontWeight={400}
            bgcolor="orange"
        >
            A new take on managing your sports club where you can digitally take control of your sports club and better drive member engagement.
        </Typography>
    )
}

export default function PromoText() {
    const classes = useStyles();

    return (
        <Box
            sx={{
                flexGrow: 1,
                padding: 8
            }}
            bgcolor="red"
        >
            <Grid container spacing={2} columns={{ xs: 4, sm: 8, md: 12 }}>
                <Grid item xs={4} sm={8} md={4}>
                    <Box>
                        <Image
                            src={HomeImage}
                            fit="scale-down"
                            sx={{
                                maxHeight: 800
                            }}
                        />
                    </Box>
                </Grid>
                <Grid item xs={4} sm={8} md={8} align="center">
                    <Box
                        height="1"
                        width="1"
                        bgcolor="blue"
                        align="center"
                    >
                        <Headline />
                        <Caption />
                    </Box>
                </Grid>
            </Grid>
        </Box>
    )
}
