import React from 'react'
import {Box, Grid, Paper, Stack, Typography} from "@mui/material";
import {makeStyles} from "@mui/styles";

const useStyles = makeStyles((theme) => ({
    textStack: {
        [theme.breakpoints.up('md')]: {
            alignContent: "center",
            justifyContent: "center",
            textAlign: "center"
        }
    }
}));

function Headline() {
    return (
        <Typography
            variant="h3"
        >
            social media, for sports clubs
        </Typography>
    )
}

function Caption() {
    return (
        <Typography
            variant="h6"
            width="100%"
        >
            A new take on managing your sports club where you can digitally take control of your sports club and better drive member engagement.
        </Typography>
    )
}

export default function PromoText() {
    const classes = useStyles();

    return (
        <Box p="10" sx={{ flexGrow: 1 }}>
            <Grid container spacing={2}>
                <Grid item xs={2}>
                    <Paper>
                        <Typography>
                            Just some text
                        </Typography>
                    </Paper>
                </Grid>
                <Grid item xs={10}>
                    <Paper>
                        <Headline />
                        <Caption />
                    </Paper>
                </Grid>
            </Grid>
        </Box>
    )
}
